mapper.select:
SELECT a.id AS "id", a.user_id, a.order_id, b.id AS "office.id", b.name AS "office.name", a.company, a.order_name, a.order_phone, a.car_number, a.qr_code_image, a.car_type, a.begin_time, a.qr_code_address, a.end_time, a.accredit_time, a.order_reason, a.pass, a.state, a.remarks, a.create_by AS "createBy.id", a.create_date, a.update_by AS "updateBy.id", a.update_date, a.del_flag, a.pay_for, a.pay_money, a.color, a.disable FROM mht_oeg.sys_orderlist a LEFT JOIN mht_oeg.sys_office b ON a.office_id = b.id WHERE a.car_number = #{carNumber} AND a.office_id=b.id
AND a.pass=#{pass}
AND a.state=#{state}
AND a.del_flag='0' ORDER BY a.update_date DESC

SELECT
FROM mht_oeg.sys_orderlist a LEFT JOIN mht_oeg.sys_office b ON a.office_id = b.id LEFT JOIN mht_oeg.sys_car_money c ON a.car_type = c.car_type_id WHERE a.office_id=b.id AND a.del_flag = '0'
AND (a.order_name like '%'||#{orderName}||'%' CONCAT('%', #{orderName}, '%') or a.order_phone like '%'||#{orderName}||'%' CONCAT('%', #{orderName}, '%') or a.car_number like '%'||#{orderName}||'%' CONCAT('%', #{orderName}, '%') or b.name like '%'||#{orderName}||'%' CONCAT('%', #{orderName}, '%') or a.order_reason like '%'||#{orderName}||'%' CONCAT('%', #{orderName}, '%') )
AND a.state =#{state}
AND a.user_id =#{userId}
AND a.car_number =#{carNumber}
AND a.color =#{color}
AND a.disable =#{disable}
AND a.car_type = #{carType}
AND a.begin_time > #{beginTime}
AND a.end_time < #{endTime}
AND a.qr_code_image IS NOT NULL
AND (a.qr_code_image IS NULL OR a.qr_code_image='')
ORDER BY a.create_date DESC

mapper.update:
UPDATE mht_oeg.sys_orderlist SET 
	del_flag = '1'
WHERE id = #{id}
